DECLARE @CurrentMigration [nvarchar](max)

IF object_id('[dbo].[__MigrationHistory]') IS NOT NULL
    SELECT @CurrentMigration =
        (SELECT TOP (1) 
        [Project1].[MigrationId] AS [MigrationId]
        FROM ( SELECT 
        [Extent1].[MigrationId] AS [MigrationId]
        FROM [dbo].[__MigrationHistory] AS [Extent1]
        WHERE [Extent1].[ContextKey] = N'E_LearnAPI.Migrations.Configuration'
        )  AS [Project1]
        ORDER BY [Project1].[MigrationId] DESC)

IF @CurrentMigration IS NULL
    SET @CurrentMigration = '0'

IF @CurrentMigration < '201712011513549_Create Results Table'
BEGIN
    IF schema_id('train') IS NULL
        EXECUTE('CREATE SCHEMA [train]')
    CREATE TABLE [train].[ELearning] (
        [Id] [int] NOT NULL IDENTITY,
        [PersonId] [int] NOT NULL,
        [PersonName] [varchar](100) NOT NULL,
        [CourseId] [int] NOT NULL,
        [CourseDesc] [varchar](100) NOT NULL,
        [Score] [int] NOT NULL,
        [MaxScore] [int],
        [PassFail] [bit],
        [Received] [datetime] NOT NULL,
        [FromADAcc] [varchar](100),
        [Processed] [bit] NOT NULL,
        [Comments] [text],
        CONSTRAINT [PK_train.ELearning] PRIMARY KEY ([Id])
    )
    CREATE TABLE [dbo].[__MigrationHistory] (
        [MigrationId] [nvarchar](150) NOT NULL,
        [ContextKey] [nvarchar](300) NOT NULL,
        [Model] [varbinary](max) NOT NULL,
        [ProductVersion] [nvarchar](32) NOT NULL,
        CONSTRAINT [PK_dbo.__MigrationHistory] PRIMARY KEY ([MigrationId], [ContextKey])
    )
    INSERT [dbo].[__MigrationHistory]([MigrationId], [ContextKey], [Model], [ProductVersion])
    VALUES (N'201712011513549_Create Results Table', N'E_LearnAPI.Migrations.Configuration',   , N'6.1.3-40302')
END

IF @CurrentMigration < '201712041353573_add other tables to DB & App'
BEGIN
    CREATE TABLE [train].[Courses] (
        [ID] [int] NOT NULL IDENTITY,
        [Course] [varchar](80) NOT NULL,
        [Paris] [bit] NOT NULL,
        [Child_Health] [bit] NOT NULL,
        CONSTRAINT [PK_train.Courses] PRIMARY KEY ([ID])
    )
    CREATE TABLE [train].[Req] (
        [ID] [int] NOT NULL IDENTITY,
        [Staff] [int] NOT NULL,
        [Course] [int] NOT NULL,
        [Status] [smallint] NOT NULL,
        [Comments] [text],
        CONSTRAINT [PK_train.Req] PRIMARY KEY ([ID])
    )
    CREATE INDEX [IX_Staff] ON [train].[Req]([Staff])
    CREATE INDEX [IX_Course] ON [train].[Req]([Course])
    CREATE INDEX [IX_Status] ON [train].[Req]([Status])
    CREATE TABLE [train].[Staff] (
        [ID] [int] NOT NULL IDENTITY,
        [Fname] [varchar](35),
        [Sname] [varchar](35) NOT NULL,
        [ADAccount] [varchar](50),
        CONSTRAINT [PK_train.Staff] PRIMARY KEY ([ID])
    )
    CREATE TABLE [train].[Statuses] (
        [ID] [smallint] NOT NULL IDENTITY,
        [Status] [varchar](50),
        CONSTRAINT [PK_train.Statuses] PRIMARY KEY ([ID])
    )
    SET IDENTITY_INSERT train.Statuses ON
    INSERT INTO train.Statuses (Id, Status) VALUES (1, 'Required')
    INSERT INTO train.Statuses (Id, Status) VALUES (2, 'Booked')
    INSERT INTO train.Statuses (Id, Status) VALUES (5, 'Completed')
    INSERT INTO train.Statuses (Id, Status) VALUES (7, 'No Longer Required')
    SET IDENTITY_INSERT train.Statuses OFF
    ALTER TABLE [train].[Req] ADD CONSTRAINT [FK_train.Req_train.Courses_Course] FOREIGN KEY ([Course]) REFERENCES [train].[Courses] ([ID]) ON DELETE CASCADE
    ALTER TABLE [train].[Req] ADD CONSTRAINT [FK_train.Req_train.Staff_Staff] FOREIGN KEY ([Staff]) REFERENCES [train].[Staff] ([ID]) ON DELETE CASCADE
    ALTER TABLE [train].[Req] ADD CONSTRAINT [FK_train.Req_train.Statuses_Status] FOREIGN KEY ([Status]) REFERENCES [train].[Statuses] ([ID]) ON DELETE CASCADE
    INSERT [dbo].[__MigrationHistory]([MigrationId], [ContextKey], [Model], [ProductVersion])
    VALUES (N'201712041353573_add other tables to DB & App', N'E_LearnAPI.Migrations.Configuration',   , N'6.1.3-40302')
END

IF @CurrentMigration < '201712041506587_Add ESRID to Person Record'
BEGIN
    ALTER TABLE [train].[Staff] ADD [ESRID] [int]
    INSERT [dbo].[__MigrationHistory]([MigrationId], [ContextKey], [Model], [ProductVersion])
    VALUES (N'201712041506587_Add ESRID to Person Record', N'E_LearnAPI.Migrations.Configuration',   , N'6.1.3-40302')
END

